import React from 'react';
import './checkboxes.module.scss';
import { ReactComponent as FiltersArrow } from '../../../assets/img/filters_arrow.svg';

const hobbies = [
    { value: 'Спортзал' },
    { value: 'Кальян' },
    { value: 'Диван' },
];

const music = [
    { value: 'Тяжелый рок', },
    { value: 'Русский рэп', },
    { value: 'Евроденс', },
];

const food = [
    { value: 'Мясоед', },
    { value: 'Сидит на ПП', },
    { value: 'Веган-сыроед', },
];

function Checkboxes() {

    return (
        <div className="filters__holder checkboxes">
            <ul className="checkboxes__list">
                <div className="checkboxes__name">
                    <p>Хобби:</p>
                    <FiltersArrow />
                </div>
                {hobbies.map(({ value }) => (
                    <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                        <label>
                            <input
                                className="checkbox__input"
                                type="checkbox"
                                value={value}
                                name={value}
                            />
                            <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                            <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                        </label>
                    </li>
                ))}
            </ul>
            <ul className="checkboxes__list">
            <p className="checkboxes__name">Музыка:</p>
                {music.map(({ value }) => (
                    <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                    <label>
                        <input
                            className="checkbox__input"
                            type="checkbox"
                            value={value}
                            name={value}
                        />
                        <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                        <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                    </label>
                </li>
                ))}
            </ul>
            <ul className="checkboxes__list">
            <p className="checkboxes__name">Еда:</p>
                {food.map(({ value }) => (
                    <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                    <label>
                        <input
                            className="checkbox__input"
                            type="checkbox"
                            value={value}
                            name={value}
                        />
                        <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                        <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                    </label>
                </li>
                ))}
            </ul>
        </div>
    );
}

export default Checkboxes;
