import React from 'react';
import './textarea-hashtags.module.scss'

function TextareaHashtags() {
  return (
    <div className="textarea-hashtags">
      <div className="textarea-hashtags__wrapper">
        <span className="textarea-hashtags__badge">ТЭГИ</span>
        <textarea 
          name="hashtags"
          className="textarea-hashtags__textarea"
        ></textarea>
      </div>
    </div>
  );
}

export default TextareaHashtags;
