import React from 'react';
import { useState, useEffect } from "react";
import './checkboxes.module.scss';
import { ReactComponent as FiltersArrow } from '../../../assets/img/filters_arrow.svg';

const hobbies = [
    { value: 'Спортзал' },
    { value: 'Кальян' },
    { value: 'Диван' },
];

const music = [
    { value: 'Тяжелый рок', },
    { value: 'Русский рэп', },
    { value: 'Евроденс', },
];

const food = [
    { value: 'Мясоед', },
    { value: 'Сидит на ПП', },
    { value: 'Веган-сыроед', },
];



function Checkboxes() {

    const [isMobile, setIsMobile] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth < 1024) {
                setIsMobile(true);
            } else {
                setIsMobile(false);
            }
        };

        handleResize();

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    const [isOpen, setIsOpen] = useState(false);
    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const [isClicked, setIsClicked] = useState(false);
    const handleButtonClick = () => {
        setIsClicked(!isClicked);
    };

    return (
        <div className="filters__wrapper checkboxes">
            <div>
                <div className={(isClicked || isMobile) ? 'checkboxes__holder checkboxes__holder--opend' : 'checkboxes__holder'}>
                    <h3 onClick={(evt) => evt.currentTarget.closest('div')?.classList.toggle('active')}>
                        <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                            <p>Хобби:</p>
                            <FiltersArrow />
                        </button >
                    </h3>
                    <ul className="checkboxes__list">
                        {hobbies.map(({ value }) => (
                            <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                                <label>
                                    <input
                                        className="checkbox__input"
                                        type="checkbox"
                                        value={value}
                                        name={value}
                                    />
                                    <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                    <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                                </label>
                            </li>
                        ))}
                    </ul>
                </div >
                <div className="filters__holder">
                    <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                        <p>Музыка:</p>
                        <FiltersArrow />
                    </button>
                    <ul className="checkboxes__list">
                        {music.map(({ value }) => (
                            <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                                <label>
                                    <input
                                        className="checkbox__input"
                                        type="checkbox"
                                        value={value}
                                        name={value}
                                    />
                                    <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                    <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                                </label>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="filters__holder">
                    <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                        <p>Еда:</p>
                        <FiltersArrow />
                    </button>

                    <ul className="checkboxes__list">
                        {food.map(({ value }) => (
                            <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                                <label>
                                    <input
                                        className="checkbox__input"
                                        type="checkbox"
                                        value={value}
                                        name={value}
                                    />
                                    <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                    <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                                </label>
                            </li>
                        ))}
                    </ul>
                </div>
            </div >

        </div >
    );
}

export default Checkboxes;
