import React from 'react';
import { useState, useEffect } from "react";
import './checkboxes.module.scss';
import { ReactComponent as FiltersArrow } from '../../../assets/img/filters_arrow.svg';

const hobbies = [
    { value: 'Спортзал' },
    { value: 'Кальян' },
    { value: 'Диван' },
];

const music = [
    { value: 'Тяжелый рок', },
    { value: 'Русский рэп', },
    { value: 'Евроденс', },
];

const food = [
    { value: 'Мясоед', },
    { value: 'Сидит на ПП', },
    { value: 'Веган-сыроед', },
];

const [isMobile, setIsMobile] = useState(false);
useEffect(() => {
    const handleResize = () => {
        if (window.innerWidth < 1024) {
            setIsMobile(true);
        } else {
            setIsMobile(false);
        }
    };

    handleResize();

    window.addEventListener('resize', handleResize);

    return () => {
        window.removeEventListener('resize', handleResize);
    };
}, []);

function Checkboxes() {

    const [isOpen, setIsOpen] = useState(false);
    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const [isClicked, setIsClicked] = useState(false);
    const handleButtonClick = () => {
        setIsClicked(!isClicked);
    };

    return (
        <div className="filters__wrapper checkboxes">
            <div className={isClicked ? 'checkboxes__holder checkboxes__holder--opend' : 'checkboxes__holder'}>
                <div className="checkboxes__name">
                    <p>Хобби:</p>
                    <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                        <FiltersArrow />
                    </button>
                </div>
                <ul className="checkboxes__list">
                    {hobbies.map(({ value }) => (
                        <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                            <label>
                                <input
                                    className="checkbox__input"
                                    type="checkbox"
                                    value={value}
                                    name={value}
                                />
                                <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                            </label>
                        </li>
                    ))}
                </ul>
            </div>
            <div className="filters__holder">
                <div className="checkboxes__name">
                    <p>Музыка:</p>
                    <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                        <FiltersArrow />
                    </button>
                </div>

                <ul className="checkboxes__list">
                    {music.map(({ value }) => (
                        <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                            <label>
                                <input
                                    className="checkbox__input"
                                    type="checkbox"
                                    value={value}
                                    name={value}
                                />
                                <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                            </label>
                        </li>
                    ))}
                </ul>
            </div>
            <div className="filters__holder">
                <div className="checkboxes__name">
                    <p>Еда:</p>
                    <button type="button" className="checkboxes__button" onClick={() => { toggleMenu(); handleButtonClick(); }}>
                        <FiltersArrow />
                    </button>
                </div>
                <ul className="checkboxes__list">
                    {food.map(({ value }) => (
                        <li className="filters__custom-toggle custom-toggle custom-toggle--checkbox is-valid" key={value}>
                            <label>
                                <input
                                    className="checkbox__input"
                                    type="checkbox"
                                    value={value}
                                    name={value}
                                />
                                <span className="custom-toggle__icon filters__custom-toggle-icon" tabIndex={0}></span>
                                <span className="custom-toggle__label filters__toggle-label">{`${value}`}</span>
                            </label>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Checkboxes;
